import React from 'react';
import PropTypes from 'prop-types';
import { format, startOfWeek, endOfWeek } from 'date-fns';

const CalendarHeader = ({ activeView, activeDate, locale }) => {
  if (activeView === 'month') {
    return (
      <h2 className="m-0">
        {format(activeDate, 'MMMM YYYY', {locale: locale})}
      </h2>
    );
  }

  return (
    <h2 className="m-0">
      {`${format(startOfWeek(activeDate), 'MMM DD', {locale: locale})} - ${format(endOfWeek(activeDate), 'MMM DD, YYYY', {locale: locale})}`}
    </h2>
  );
}

CalendarHeader.propTypes = {
  activeView: PropTypes.string.isRequired,
  activeDate: PropTypes.instanceOf(Date).isRequired,
  locale: PropTypes.object.isRequired
}

export default CalendarHeader;