import React from 'react';
import PropTypes from 'prop-types';
import { MDBBtn, MDBBtnGroup } from 'mdbreact';

const SwitchButtons = ({ active, leftEvt, middleEvt, rightEvt, texts, btnSizes, btnSizesClassName, btnSizesColors, ...props }) => {
  return (
    <MDBBtnGroup size={btnSizes} className={btnSizesClassName} {...props}>
      <MDBBtn color={btnSizesColors[0]} outline={!(active === 0)} onClick={leftEvt}>
        {texts[0]}
      </MDBBtn>
      <MDBBtn color={btnSizesColors[1]} outline={!(active === 1)} onClick={middleEvt}>
        {texts[1]}
      </MDBBtn>
      <MDBBtn color={btnSizesColors[2]} outline={!(active === 2)} onClick={rightEvt}>
        {texts[2]}
      </MDBBtn>
    </MDBBtnGroup>
  );
};

SwitchButtons.propTypes = {
  leftEvt: PropTypes.func.isRequired,
  middleEvt: PropTypes.func.isRequired,
  rightEvt: PropTypes.func.isRequired,
  texts: PropTypes.arrayOf(PropTypes.oneOfType([PropTypes.object, PropTypes.string])).isRequired,
  active: PropTypes.number,
};

export default SwitchButtons;
