import { isWithinRange, isSameWeek } from "date-fns";

export default function addTasksToWeek(week, tasks) {
  const weekTasks = tasks.filter(
    task =>
      isSameWeek(week.days[0], task.startDate) ||
      isSameWeek(week.days[0], task.endDate) ||
      isWithinRange(week.days[0], task.startDate, task.endDate)
  );

  week.tasks = weekTasks;
  return week;
}
