import { subMonths, addMonths, subWeeks, addWeeks, subDays, addDays } from "date-fns";

export default function changeDate(activeView, activeDate, amount) {
  let newDate = new Date();

  if(amount === 0) {
    return newDate;
  }
  
  switch(activeView) {
    case 'month': 
      newDate = changeMonth(activeDate, amount);
      break;
    case 'week':
    case 'list':
      newDate = changeWeek(activeDate, amount);
      break;
    case 'day':
      newDate = changeDay(activeDate, amount);
      break;
    default: break;
  }

  return newDate;
}

function changeMonth(date, amount) {
  const newDate = amount < 0 ? subMonths(date, -amount) : addMonths(date, amount);
  return newDate; 
}

function changeWeek(date, amount) {
  const newDate = amount < 0 ? subWeeks(date, -amount) : addWeeks(date, amount);
  return newDate; 
}

function changeDay(date, amount) {
  const newDate = amount < 0 ? subDays(date, -amount) : addDays(date, amount);
  return newDate; 
}
