import React, { PureComponent } from "react";
import { findDOMNode } from 'react-dom'
import PropTypes from "prop-types";

class ClickOutsideListener extends PureComponent {

    componentDidMount() {
        this.ref = findDOMNode(this);
        document.addEventListener('click', this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener('click', this.handleClickOutside);
    }

    handleClickOutside = e => {
        if (this.ref && this.ref.contains) {
            const clickedOutside = !this.ref.contains(e.target);

            if (clickedOutside) {
                this.props.close();
            }
        }
    }

    render() {
        return this.props.children
    }
}

ClickOutsideListener.propTypes = {
    close: PropTypes.func.isRequired
}

export default ClickOutsideListener;

