import React from 'react';
import PropTypes from "prop-types";

const colorsPreview = props => {
    return (
        <div className="mdb-cp-color-preview">
            <div className="mdb-cp-last-color" style={{ background: `${props.lastColor}`, width: '2em' }} onClick={props.setLastColor}></div>
            <div className="mdb-cp-current-color" style={{ background: `${props.selectedColor}`, width: '2em' }}></div>
        </div>
    );
};

colorsPreview.propTypes = {
    lastColor: PropTypes.string.isRequired,
    selectedColor: PropTypes.string.isRequired,
    setLastColor: PropTypes.func.isRequired
}

export default colorsPreview;