import React from 'react';
import PropTypes from 'prop-types'

const errors = props => {
    const { errorMessagesArray } = props
    return (
        <div data-test="component-list" className="file-upload-errors-container">
            <ul>
                {errorMessagesArray.map(message => <li data-test="component-list-item" key={message}>{message}</li>)}
            </ul>
        </div>
    );
};

errors.propTypes = {
    errorMessagesArray: PropTypes.arrayOf(PropTypes.string).isRequired
}

export default errors;