import React from "react";
import PropTypes from "prop-types";

class Fa extends React.Component {
    render() {
        const {
            border,
            brand,
            className,
            fab,
            fal,
            far,
            fixed,
            flip,
            icon,
            inverse,
            light,
            list,
            pull,
            pulse,
            regular,
            rotate,
            size,
            spin,
            stack,
            ...attributes
        } = this.props;

        const iconPrefix = (brand || fab) ? "fab" : (light || fal) ? "fal" : (regular || far) ? "far" : "fa";

        const classes = [];

        classes.push(iconPrefix);
        list && classes.push("fa-li");
        icon && classes.push(`fa-${icon}`);
        size && classes.push(`fa-${size}`);
        fixed && classes.push("fa-fw");
        pull && classes.push(`fa-pull-${pull}`);
        border && classes.push("fa-border");
        spin && classes.push("fa-spin");
        pulse && classes.push("fa-pulse");
        rotate && classes.push(`fa-rotate-${rotate}`);
        flip && classes.push(`fa-flip-${flip}`);
        inverse && classes.push("fa-inverse");
        stack && classes.push(`fa-${stack}`);
        className && classes.push(className);

        return <i {...attributes} className={classes.join(' ')} />;
    }
}

Fa.propTypes = {
    icon: PropTypes.string.isRequired,
    border: PropTypes.bool,
    brand: PropTypes.bool,
    className: PropTypes.string,
    fab: PropTypes.bool,
    fal: PropTypes.bool,
    far: PropTypes.bool,
    fixed: PropTypes.bool,
    flip: PropTypes.string,
    inverse: PropTypes.string,
    light: PropTypes.bool,
    list: PropTypes.bool,
    pull: PropTypes.string,
    pulse: PropTypes.bool,
    regular: PropTypes.bool,
    rotate: PropTypes.string,
    spin: PropTypes.bool,
    size: PropTypes.string,
    stack: PropTypes.string
};

Fa.defaultProps = {
    border: false,
    brand: false,
    className: "",
    fab: false,
    fal: false,
    far: false,
    fixed: false,
    flip: "",
    inverse: "",
    light: false,
    list: false,
    pull: "",
    pulse: false,
    regular: false,
    rotate: "",
    spin: false,
    size: "",
    stack: ""
}

export default Fa;
export { Fa as MDBIcon };
