import React from 'react';
import PropTypes from 'prop-types'
import MDBIcon from './icon';

const message = props => {
    const { hasError, messageError, messageDefault } = props;

    return (
        <div data-test="component-message" className="card-text file-upload-message">
            <MDBIcon data-test="component-icon" icon="cloud-upload-alt" />
            <p data-test="component-message-default">{messageDefault}</p>
            {hasError && <p data-test="component-message-error" className="file-upload-error">{messageError}</p>}
        </div>
    );
};

message.propTypes = {
    hasError: PropTypes.bool.isRequired,
    messageError: PropTypes.string.isRequired,
    messageDefault: PropTypes.string.isRequired,
}

export default message;