import React from 'react';
import PropTypes from "prop-types";
import MDBIcon from './icon';

const preview = props => {
    const { src, fileExt, name, messageReplace } = props;

    return (
        <div data-test="component-preview-wrapper" className="file-upload-preview">
            <span className="file-upload-render">
                {
                    src
                        ? <img data-test="component-preview-img" className="file-upload-preview-img" alt="" src={src} />
                        : <><MDBIcon data-test="component-preview-icon" icon="file" /><span data-test="component-preview-ext" className="file-upload-extension">{fileExt}</span></>
                }
            </span>
            <div className="file-upload-infos">
                <div className="file-upload-infos-inner">
                    <p className="file-upload-filename">
                        <span data-test="component-preview-name" className="file-upload-filename-inner">{name}</span>
                    </p>
                    <p data-test="component-preview-msg" className="file-upload-infos-message">{messageReplace}</p>
                </div>
            </div>
        </div>
    );
};

preview.propTypes = {
    src: PropTypes.string,
    fileExt: PropTypes.string,
    name: PropTypes.string.isRequired,
    messageReplace: PropTypes.string.isRequired,
}

export default preview;


