import React from 'react';
import PropTypes from 'prop-types'
import MDBIcon from './icon';
import MDBBtn from './btn';

const button = props => {
    const { onClick, messageRemove } = props;

    return (
        <MDBBtn
            data-test="component-remove-btn"
            onClick={onClick}
            size="sm"
            color="danger"
        >
            {messageRemove}
            <MDBIcon data-test="component-icon" className="ml-1" far icon="trash-alt" />
        </MDBBtn>
    );
};

button.propTypes = {
    onClick: PropTypes.func.isRequired,
    messageRemove: PropTypes.string.isRequired
}

export default button;