import React from 'react';
import PropTypes from 'prop-types'
import MDBIcon from './icon';
import MDBBtn from './btn';

const submitButton = props => {
    const { onClick, messageUpload } = props;

    return (
        <MDBBtn
            data-test="component-submit-btn"
            onClick={onClick}
            size="sm"
            color="success"
            type="submit"
        >
            {messageUpload}
            <MDBIcon data-test="component-icon" className="ml-1" far icon="paper-plane" />
        </MDBBtn>
    );
};

submitButton.propTypes = {
    onClick: PropTypes.func.isRequired,
    messageUpload: PropTypes.string.isRequired,
}

export default submitButton;