export const getImageFormat = (width, height) => {
    let imgFormat;

    if (width === height) {
        imgFormat = 'square';
    }

    if (width < height) {
        imgFormat = 'portrait';
    }

    if (width > height) {
        imgFormat = 'landscape';
    }

    return imgFormat;
};

export const convertSizeToByte = size => {
    let value = 0;

    if (size !== 0) {
        const unit = size.slice(-1).toUpperCase();

        const kb = 1024;
        const mb = kb * 1024;
        const gb = mb * 1024;

        if (unit === 'K') {
            value = parseFloat(size) * kb;
        } else if (unit === 'M') {
            value = parseFloat(size) * mb;
        } else if (unit === 'G') {
            value = parseFloat(size) * gb;
        }
    }

    return value;
};

export const getFileType = name => {
    return name.split('.').pop().toLowerCase();
}
