import React from 'react';
import PropTypes from 'prop-types';

const controlers = ({ changeCategory, categories }) => {
    return (
        < div className="filter-nav" >
            <button id="btn" className="btn btn-success active" onClick={changeCategory} data-filter="all">All</button>
            {
                categories.map(category => (
                    <button key={category} className="btn btn-primary" onClick={changeCategory} data-filter={category}>{category}</button>
                ))
            }
        </div >
    );
};

controlers.propTypes = {
    categories: PropTypes.arrayOf(PropTypes.string),
    changeCategory: PropTypes.func
}


export default controlers;