import React from "react";
import { SortableElement } from "react-sortable-hoc";
import PropTypes from "prop-types";
import "./index.css";

const SortableItem = SortableElement(
  ({ value, itemClassName, disabledClassName, disabled }) => {
    const classnames = [];
    itemClassName && classnames.push(itemClassName);
    disabled && disabledClassName && classnames.push(disabledClassName);

    return (
      <li data-sortable tabIndex={0} className={classnames.join(" ")}>
        {value}
      </li>
    );
  }
);

export default SortableItem;

SortableItem.propTypes = {
  itemClassName: PropTypes.string,
  disabledClassName: PropTypes.string,
  disabled: PropTypes.bool
};
